import axios from "axios";
import MISAEnum from "@/script/constants/MISAEnum.js";
import MISAResource from "../../script/constants/MISAResource.js";
import DxSelectBox from "devextreme-vue/select-box";
import DxTagBox from "devextreme-vue/tag-box";
import DxDateBox from "devextreme-vue/date-box";
import BasePopupChange from "../base/BasePopupChange.vue";
import BaseApi from '../../script/constants/BaseApi';
export default {
  components: {
    DxSelectBox,
    DxTagBox,
    DxDateBox,
    BasePopupChange
  },
  props: ["isShow", "staffSelected", "staffSelectedId", "btnSaveMode", "dialogName"],

  data() {
    return {
      isHideLoader: true,
      isHidePopup: true,
      popupTitle: "Thông báo",
      popupText: "Dữ liệu đã thay đổi, bạn có muốn lưu lại không?",
      staff: {},
      isTrained: null,
      isWorking: null,
      quitWorkDate: null,
      teacherGetAll: null,
      departments: {},
      subjects: {},
      subjectIdList: [],
      teacherSubject: {},
      rooms: {},
      roomIdList: [],
      teacherRoom: {},
      isChangeData: false,
      oldTeacher: {},
      minDate: new Date(1975, 1, 1),
      now: Date()
    };
  },

  created() {
    this.Resource = MISAResource;
    var me = this;
    /**
     * Gọi api lấy dữ liệu các môn học
     * Author: VQBao - 15/8/2022
     */

    axios.get(BaseApi.SubjectApi).then(function (response) {
      me.subjects = response.data;
    }).catch(function (err) {
      console.log(err);
    });
    /**
     * Gọi api lấy dữ liệu các tổ bộ môn
     * Author: VQBao - 15/8/2022
     */

    axios.get(BaseApi.DepartmentApi).then(function (response) {
      me.departments = response.data;
    }).catch(function (err) {
      console.log(err);
    });
    /**
     * Gọi api lấy dữ liệu các kho phòng
     * Author: VQBao - 15/8/2022
     */

    axios.get(BaseApi.RoomApi).then(function (response) {
      me.rooms = response.data;
    }).catch(function (err) {
      console.log(err);
    });
  },

  watch: {
    isShow: function (value) {
      // khi form thông tin chi tiết mở ra, focus vào ô input đầu tiên
      if (value == true) {
        this.$nextTick(() => {
          this.$refs.staffCode.focus();
        }); // khi mở form thêm mới, reset lại các trường thông tin

        if (this.btnSaveMode == MISAEnum.EditMode.Add) {
          this.isTrained = false;
          this.isWorking = false;
          this.quitWorkDate = null;
          this.roomIdList = [];
          this.subjectIdList = [];
        }

        this.isChangeData = false;
      }
    },
    staffSelected: function (value) {
      this.staff = value;
    },
    staffSelectedId: function (value) {
      var me = this; // biến danh sách các id của môn học, kho phòng

      var subjectList = [];
      var roomList = [];

      if (value) {
        /**
         * gọi api lấy dữ liệu thông tin chi tiết 1 giáo viên
         * Author: VQBao - 21/7/2022
         */
        axios.get(BaseApi.TeacherApi + `/${me.staffSelectedId}`).then(function (response) {
          switch (response.status) {
            case 200:
              me.staff = response.data.teacher;
              me.isTrained = response.data.teacher.isTrained;
              me.isWorking = response.data.teacher.isWorking;
              me.quitWorkDate = response.data.teacher.quitWorkDate;
              me.teacherRoom = response.data.room;
              me.teacherSubject = response.data.subject;

              for (let i = 0; i < me.teacherSubject.length; i++) {
                subjectList.push(me.teacherSubject[i].subjectId);
              }

              me.subjectIdList = subjectList;

              for (let i = 0; i < me.teacherRoom.length; i++) {
                roomList.push(me.teacherRoom[i].roomId);
              }

              me.roomIdList = roomList; // xây dựng object lưu thông tin khi api trả về chưa thay đổi

              me.oldTeacher = {
                teacher: {
                  teacherCode: response.data.teacher.teacherCode,
                  fullName: response.data.teacher.fullName,
                  phoneNumber: response.data.teacher.phoneNumber,
                  email: response.data.teacher.email,
                  departmentId: response.data.teacher.departmentId,
                  isTrained: response.data.teacher.isTrained,
                  isWorking: response.data.teacher.isWorking,
                  quitWorkDate: response.data.teacher.quitWorkDate
                },
                roomList: me.roomIdList,
                subjectList: me.subjectIdList
              };
              break;
          }
        }).catch(function (res) {
          switch (res.status) {
            case 404:
              console.log("404 Not Found");
              break;
          }
        });
      }
    }
  },
  methods: {
    /**
     * Hiển thị toast thành công
     * Author: VQBao - 15/8/2022
     */
    showToast() {
      if (this.btnSaveMode == MISAEnum.EditMode.Edit) {
        this.$emit("hideToastMessage", MISAResource.TOAST.Edit.Title, MISAResource.TOAST.Edit.Message);
      } else {
        this.$emit("hideToastMessage", MISAResource.TOAST.Add.Title, MISAResource.TOAST.Add.Message);
      }
    },

    /**
     * Ẩn form thông tin chi tiết
     * Author: VQBao - 15/8/2022
     */
    hideDialog() {
      this.$emit("isShowStaffDialog", false);
    },

    /**
     * Ẩn popup thông báo
     * Author: VQBao - 15/8/2022
     */
    hidePopup(isHide) {
      this.isHidePopup = isHide;
    },

    /**
     * khi click vào nút đóng thì đóng form giáo viên chi tiết
     * Author: VQBao - 21/7/2022
     */
    btnCloseOnClick() {
      // nếu đang sửa thì check thôn tin hiện tại có thay đổi so với thông tin ban đầu không, nếu có thì hiển thị popup
      if (this.btnSaveMode == MISAEnum.EditMode.Edit) {
        if (this.oldTeacher.teacher.teacherCode != this.staff.teacherCode || this.oldTeacher.teacher.fullName != this.staff.fullName || this.oldTeacher.teacher.phoneNumber != this.staff.phoneNumber || this.oldTeacher.teacher.email != this.staff.email || this.oldTeacher.teacher.departmentId != this.staff.departmentId || this.oldTeacher.teacher.isTrained != this.isTrained || this.oldTeacher.teacher.isWorking != this.isWorking || this.oldTeacher.teacher.quitWorkDate != this.quitWorkDate || this.oldTeacher.roomList != this.roomIdList || this.oldTeacher.subjectList != this.subjectIdList) {
          this.isHidePopup = false;
        } else {
          this.$emit("isShowStaffDialog", false);
        }
      } // nếu đang thêm mới thì kiểm tra có trường thông tin nào khác rỗng không, nếu có thì hiển thị popup
      else {
        if (this.staff.teacherCode != null || this.staff.fullName != null || this.staff.phoneNumber != null || this.staff.email != null || this.staff.departmentId != null || this.staff.isTrained == true || this.staff.isWorking == true || this.quitWorkDate != null || this.roomIdList.length > 0 || this.subjectIdList.length > 0) {
          this.isHidePopup = false;
        } else {
          this.$emit("isShowStaffDialog", false);
        }
      }
    },

    /**
     * Thực hiện quay lại giá trị ban đầu khi không đồng ý sửa thông tin giáo viên
     * Author: VQBao - 15/8/2022
     */
    rollBackOldTeacher() {
      this.staff.teacherCode = this.oldTeacher.teacher.teacherCode;
      this.staff.fullName = this.oldTeacher.teacher.fullName;
      this.staff.phoneNumber = this.oldTeacher.teacher.phoneNumber;
      this.staff.email = this.oldTeacher.teacher.email;
      this.staff.departmentId = this.oldTeacher.teacher.departmentId;
      this.staff.isTrained = this.oldTeacher.teacher.isTrained;
      this.staff.isWorking = this.oldTeacher.teacher.isWorking;
      this.quitWorkDate = this.oldTeacher.teacher.quitWorkDate;
      this.roomIdList = this.oldTeacher.roomList;
      this.subjectIdList = this.oldTeacher.subjectList;
    },

    /**
     * Thực hiện cất dữ liệu
     * Author: VQBao - 21/7/2022
     */
    saveStaffOnClick() {
      var me = this;
      me.isHideLoader = false;

      if (me.isTrained) {
        me.staff.isTrained = 1;
      } else {
        me.staff.isTrained = 0;
      }

      if (me.isWorking) {
        me.staff.isWorking = 1;
      } else {
        me.staff.isWorking = 0;
      }

      me.staff.roomId = me.roomIdList;
      me.staff.subjectId = me.subjectIdList; // 1. validate dữ liệu, nếu dữ liệu hợp lệ thì gọi api thêm mới giáo viên

      var isValid = me.validateStaffData();

      if (isValid) {
        // 2. gọi api thực hiên thêm mới dữ liệu
        // nếu saveMode là Add thì thực hiện thêm mới, không thì thực hiện sửa
        if (this.btnSaveMode == MISAEnum.EditMode.Add) {
          if (me.quitWorkDate != null) {
            me.quitWorkDate.setDate(me.quitWorkDate.getDate() + 1);
            me.staff.quitWorkDate = me.quitWorkDate;
          }

          axios.post(Bas, me.staff).then(function () {
            // ẩn loading
            me.isHideLoader = true; // tắt form sửa thông tin

            me.$emit("isShowStaffDialog", false); // hiển thị toast message thành công

            me.$emit("hideToastMessage", MISAResource.TOAST.Add.Title, MISAResource.TOAST.Add.Message); // load lại dữ liệu của bảng

            me.$emit("loadData", 25, 1, "");
          }).catch(function (err) {
            console.log(err);
          });
        } else {
          if (me.staffSelected.quitWorkDate == null && me.quitWorkDate != null) {
            me.quitWorkDate.setDate(me.quitWorkDate.getDate() + 1);
            me.staff.quitWorkDate = me.quitWorkDate;
          } else {
            me.staff.quitWorkDate = me.quitWorkDate;
          }

          axios.put(`https://localhost:44323/api/v1/Teachers/${me.staffSelectedId}`, me.staff).then(function () {
            // ẩn loading
            me.isHideLoader = true; // tắt form sửa thông tin

            me.$emit("isShowStaffDialog", false); // hiển thị toast message thành công

            me.$emit("hideToastMessage", MISAResource.TOAST.Edit.Title, MISAResource.TOAST.Edit.Message); // load lại dữ liệu của bảng

            me.$emit("loadData", 25, 1, "");
          }).catch(function (err) {
            console.log(err);
          });
        }
      }
    },

    /**
     * Khi blur, nếu mã giáo viên để trống thì hiện lỗi
     * Author: VQBao - 27/7/2022
     */
    validateStaffCode() {
      if (!this.staff.teacherCode) {
        this.$refs.staffCode.classList.add("input-error");
        this.$refs.staffCode.setAttribute("title", MISAResource.ERROR.EmptyStaffCode);
      } else {
        this.$refs.staffCode.classList.remove("input-error");
        this.$refs.staffCode.setAttribute("title", "");
        this.imageStaffCode = this.staff.teacherCode;
      }
    },

    /**
     * Khi blur khỏi input Họ tên, nếu họ tên trống thì báo lỗi
     */
    validateFullName() {
      if (!this.staff.fullName) {
        this.$refs.staffName.classList.add("input-error");
        this.$refs.staffName.setAttribute("title", MISAResource.ERROR.EmptyFullName);
      } else {
        this.$refs.staffName.classList.remove("input-error");
        this.$refs.staffName.setAttribute("title", "");
        this.imageStaffName = this.staff.fullName;
      }
    },

    /**
     * Khi blur, nếu email không đúng định dạng thì báo lỗi
     * Author: VQBao - 27/7/2022
     */
    validateEmail() {
      var email = event.currentTarget.value;

      if (email) {
        var emailFormat = /^([a-zA-Z0-9_\-.]+)@([a-zA-Z0-9_\-.]+)\.([a-zA-Z]{2,5})$/;

        if (!emailFormat.test(email)) {
          event.currentTarget.classList.add("input-error");
          event.currentTarget.setAttribute("title", MISAResource.ERROR.InValidEmail);
        } else {
          event.currentTarget.classList.remove("input-error");
          event.currentTarget.setAttribute("title", "");
        }
      } else {
        event.currentTarget.classList.remove("input-error");
        event.currentTarget.setAttribute("title", "");
      }
    },

    /**
     * Kiểm tra độ dài số điện thoại
     * Author: VQBao - 27/7/2022
     */
    validatePhoneNumber() {
      var phone = event.currentTarget.value;

      if (phone.length < 10 && phone.length > 0) {
        event.currentTarget.classList.add("input-error");
        event.currentTarget.setAttribute("title", MISAResource.ERROR.ShortPhoneLength);
      } else {
        event.currentTarget.classList.remove("input-error");
        event.currentTarget.setAttribute("title", "");
      }
    },

    /**
     * Thực hiện validate dữ liệu
     * Author: VQBao - 27/7/2022
     */
    validateStaffData() {
      var isValid = true; // validate mã giáo viên

      if (!this.staff.teacherCode) {
        isValid = false;
        this.$refs.staffCode.classList.add("input-error");
        this.$refs.staffCode.setAttribute("title", MISAResource.ERROR.EmptyStaffCode);
      } // validate họ và tên


      if (!this.staff.fullName) {
        isValid = false;
        this.$refs.staffName.classList.add("input-error");
        this.$refs.staffName.setAttribute("title", MISAResource.ERROR.EmptyFullName);
      } // validate email


      if (this.staff.email) {
        var email = this.staff.email;

        if (email) {
          var emailFormat = /^([a-zA-Z0-9_\-.]+)@([a-zA-Z0-9_\-.]+)\.([a-zA-Z]{2,5})$/;

          if (!emailFormat.test(email)) {
            isValid = false;
            this.$refs.staffEmail.classList.add("input-error");
            this.$refs.staffEmail.setAttribute("title", MISAResource.ERROR.InValidEmail);
          }
        }
      } // validate số điện thoại


      if (this.staff.phoneNumber) {
        var phone = this.staff.phoneNumber;

        if (phone.length < 10 && phone.length > 0) {
          isValid = false;
          event.currentTarget.classList.add("input-error");
          event.currentTarget.setAttribute("title", MISAResource.ERROR.ShortPhoneLength);
        } else {
          event.currentTarget.classList.remove("input-error");
          event.currentTarget.setAttribute("title", "");
        }
      } // check trùng mã
      // if (this.staff.teacherCode) {
      //   var me = this;
      //   axios
      //     .get("https://localhost:7070/api/v1/Teachers")
      //     .then(function (response) {
      //       me.teacherGetAll = response.data;
      //     })
      //     .catch(function (err) {
      //       console.log(err);
      //     });
      //   for (let i = 0; i < me.teacherGetAll.length; i++) {
      //     if (me.staff.teacherCode == me.teacherGetAll[i].teacherCode) {
      //       isValid = false;
      //       event.currentTarget.classList.add("input-error");
      //       event.currentTarget.setAttribute(
      //         "title",
      //         MISAResource.ERROR.DuplicatedCode,
      //       );
      //     } else {
      //       event.currentTarget.classList.remove("input-error");
      //       event.currentTarget.setAttribute("title", "");
      //     }
      //   }
      // }


      return isValid;
    },

    /**
     * Hàm check trình độ quản lý thiết bị
     * Author: VQBao - 28/7/2022
     */
    checkIsTrained() {
      this.isTrained = !this.isTrained;
    },

    /**
     * Hàm check trạng thái làm việc
     * Author: VQBao - 28/7/2022
     */
    checkIsWorking() {
      this.isWorking = !this.isWorking;
    }

  }
};
