import axios from "axios";
/*
 ** Xử lý các thao tác gửi dữ liệu lên Api
 ** Author VQBao(12/8/2022)
 */
export default function baseApi(method, url, data) {
  try {
    var config = {
      method: method,
      url: url,
      headers: {
        "Content-Type": "application/json",
        accept: "application/json",
      },
      data: data,
    };
    axios(config)
      .then(function (res) {
        return res;
        console.log(res);
      })
      .catch(function (res) {
        console.log(res);
      });
  } catch (er) {
    console.log(er);
    return 0;
  }
}
